import {useEffect , useState} from 'react';

import MovieCard from './MovieCard';

import './App.css';
import SearchIcon from './search.svg'
//9a48e17e
const API_URL = 'http://www.omdbapi.com?apikey=9a48e17e';

const movie1 = 
    {
        "Title": "Little Nemo: Adventures in Slumberland",
        "Year": "1989",
        "imdbID": "tt0104740",
        "Type": "movie",
        "Poster": "https://m.media-amazon.com/images/M/MV5BMmNkM2MzNDgtNzk3Ny00ZTZmLTg4MDMtODc5M2Y4ZGQ4MGMyXkEyXkFqcGdeQXVyNjMwMjk0MTQ@._V1_SX300.jpg"
    }

const App = () =>{
    const [movies, setMovies] = useState([]);

    const [searchTerm,setSearchTerm] = useState([]);

    const searchMovies = async (title) =>{
        const response = await fetch(`${API_URL}&s=${title}`);
        const data = await response.json();
        setMovies(data.Search);
    }   

useEffect ( ()=>{
    searchMovies('nemo');
    },[]);

    return(
        <div className="app">
            <h1>MovieLand</h1>

            <div className='search'>
            <input
            placeholder='Search for Movies'
            value={searchTerm}
            onChange={ (e)=>setService(e.target.value)}
            />
            <img src={SearchIcon} alt="search"
            onClick={() => {}}
            />
            </div>

            {movies.length >0  
                ? (
                    <div className="container" >
                        {movies.map((movie)=>(
                                <MovieCard movie={movie}/>
                            ))}
                    </div>
                ):
                (
                    <div className ="empty">
                        <h2>No movies found</h2>
                    </div>
                )
            }

            
        </div>
    );
}

export default App;
